(function() {
  "use strict";
  var app;

  app = angular.module("unwatched.directives", []);

  app.directive("inputMatch", [
    function() {
      return {
        require: "ngModel",
        link: function(scope, elem, attrs, ctrl) {
          var originInput;
          originInput = "#" + attrs.inputMatch;
          elem.add(originInput).on("input", function() {
            scope.$apply(function() {
              var v;
              v = elem.val() === $(originInput).val();
              console.log("asdf");
              ctrl.$setValidity("inputMatch", v);
            });
          });
        }
      };
    }
  ]);

  app.directive("member", [
    "$window", function($window) {
      return {
        templateUrl: "/partials/member.jade",
        link: function(scope, elem) {}
      };
    }
  ]);

  app.directive("updateScrollPosition", [
    function() {
      return {
        link: function(scope, elem, attrs) {
          return scope.$watch(attrs.updateScrollPosition, function() {
            return window.setTimeout((function() {
              return $(elem).parent().scrollTop($(elem).height());
            }), 1);
          });
        }
      };
    }
  ]);

  app.directive("adjustWidth", [
    "$window", function($window) {
      return {
        link: function(scope, elem, attrs) {
          var adjustElementWidth;
          adjustElementWidth = function(elem, attrs) {
            var adjustedWidth, elems;
            adjustedWidth = $(elem).find("ul").width();
            elems = $(elem).find("ul li").length;
            if (elems > 0) {
              adjustedWidth = (adjustedWidth / elems) - 2;
            }
            $(elem).find("ul li a").css("width", adjustedWidth);
          };
          scope.$watch(attrs.adjustWidth, function() {
            return window.setTimeout((function() {
              return adjustElementWidth(elem, attrs);
            }), 1);
          });
          return angular.element($window).bind("resize", function() {
            adjustElementWidth(elem, attrs);
          });
        }
      };
    }
  ]);

  app.directive("delayDisplay", [
    function() {
      return {
        link: function(scope, elem, attrs) {
          return scope.$watch(attrs.delayDisplay, function() {
            return $(elem).addClass("show");
          });
        }
      };
    }
  ]);

  app.directive("focusOnClick", [
    function() {
      return {
        link: function(scope, elem, attrs) {
          var focusField;
          focusField = "#" + attrs.focusOnClick;
          elem.on("click", function() {
            $(focusField).focus();
          });
        }
      };
    }
  ]);

  app.directive("appVersion", [
    "version", function(version) {
      return function(scope, elem, attrs) {
        return elem.text(version);
      };
    }
  ]);

  app.directive("centerVertical", [
    "$window", function($window) {
      return {
        link: function(scope, elem, attrs) {
          var centerVertical;
          centerVertical = function(elem, attrs) {
            var marginTop;
            marginTop = ($($window).height() - $(elem).height()) / 2;
            $(elem).css("margin-top", marginTop);
          };
          scope.$watch(attrs.adjustWidth, function() {
            return window.setTimeout((function() {
              return centerVertical(elem, attrs);
            }), 1);
          });
          return angular.element($window).bind("resize", function() {
            centerVertical(elem, attrs);
          });
        }
      };
    }
  ]);

}).call(this);

(function() {
  "use strict";
  var app;

  app = angular.module("unwatched", ["ngRoute", "ngAnimate", "unwatched.services", "unwatched.directives", "unwatched.filters", "unwatched.controllers", "ui.tinymce", "ui.bootstrap", "ngFitText"]);

  app.config([
    "$routeProvider", "$locationProvider", function($routeProvider, $locationProvider) {
      $locationProvider.html5Mode(true);
      $locationProvider.hashPrefix("!");
      $routeProvider.when("/", {
        templateUrl: "partials/index.jade",
        controller: "IndexCtrl"
      });
      $routeProvider.when("/spacelab", {
        templateUrl: "partials/spacelab.jade",
        controller: "SpacelabCtrl"
      });
      $routeProvider.when("/room", {
        templateUrl: "partials/room.jade",
        controller: "RoomCtrl"
      });
      $routeProvider.when("/members", {
        templateUrl: "partials/members.jade",
        controller: "MemberCtrl"
      });
      return $routeProvider.otherwise({
        redirectTo: "/"
      });
    }
  ]);

}).call(this);
